import React, { useState } from "react";
import {
  ListItem,
  ListItemText,
  Checkbox,
  IconButton,
  ListItemSecondaryAction,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import { useDispatch } from "react-redux";
import { deleteTodo, updateTodo, toggleTodo } from "../redux/taskSlice";

function TodoItem({ id, task, completed }) {
  const dispatch = useDispatch();
  // const [editable, setEditable] = useState(false);
  const [checked, setChecked] = useState(false);

  const handleDeleteTodo = () => {
    dispatch(
      deleteTodo({
        id: id,
      })
    );
  };

  const handleUpdate = () => {
    setEditable(true);
    dispatch(
      updateTodo({
        id: id,
        task: task
      })
    );
  };

  const handleToggle = () => {
    setChecked(!checked);
    dispatch(
      toggleTodo({
        id: id,
        completed: completed,
      })
    );
  };
  
  return (
    // <li className="todo-item">
    //   <div contentEditable={editable}>{task}</div>
    //   <div>
    //     <button className="update-todo" onClick={handleUpdate}>
    //       Update Todo
    //     </button>
    //     <button className="delete-todo" onClick={handleDeleteTodo}>
    //       Delete
    //     </button>
    //   </div>
    // </li>
    <ListItem>
      <Checkbox tabIndex={-1} checked={checked} onClick={handleToggle} />
      <ListItemText
        style={{ textDecoration: checked ? "line-through" : "none" }}
      >
        {task}
      </ListItemText>
      <ListItemSecondaryAction>
        <IconButton aria-label="Delete" onClick={handleDeleteTodo}>
          <DeleteIcon />
        </IconButton>
        <IconButton aria-label="Edit" onClick={() => {}}>
          <EditIcon />
        </IconButton>
      </ListItemSecondaryAction>
    </ListItem>
  );
}

export default TodoItem;
