import React, { useState } from "react";
import {
  ListItem,
  ListItemText,
  Checkbox,
  IconButton,
  ListItemSecondaryAction,
} from "@material-ui";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import { useDispatch } from "react-redux";
import { deleteTodo, updateTodo } from "../redux/taskSlice";

function TodoItem({ id, task }) {
  const dispatch = useDispatch();
  const [editable, setEditable] = useState(false);

  const handleDeleteTodo = () => {
    dispatch(
      deleteTodo({
        id: id,
      })
    );
  };

  const handleUpdate = () => {
    setEditable(true);
    dispatch(
      updateTodo({
        id: id,
      })
    );
  };
  return (
    // <li className="todo-item">
    //   <div contentEditable={editable}>{task}</div>
    //   <div>
    //     <button className="update-todo" onClick={handleUpdate}>
    //       Update Todo
    //     </button>
    //     <button className="delete-todo" onClick={handleDeleteTodo}>
    //       Delete
    //     </button>
    //   </div>
    // </li>
    <ListItem>
      <Checkbox
        tabIndex={-1}
        // checked={completed}
        onClick={() => {}}
      />
      <ListItemText
        style={{ textDecoration: completed ? "line-through" : "none" }}
      >
        {task}
      </ListItemText>
      <ListItemSecondaryAction>
        <IconButton
          aria-label="Delete"
          onClick={handleDeleteTodo}
        >
          <DeleteIcon />
        </IconButton>
        <IconButton aria-label="Edit" onClick={() => {}}>
          <EditIcon />
        </IconButton>
      </ListItemSecondaryAction>
    </ListItem>
  );
}

export default TodoItem;
